#!/bin/bash
# Generate simple PNG icons using ImageMagick or create placeholder PNGs

# Create 16x16 icon
convert -size 16x16 xc:'#667eea' \
  -fill white -gravity center -pointsize 10 -annotate 0 'L' \
  icon16.png 2>/dev/null || \
  echo '' | base64 -d > icon16.png

# Create 48x48 icon  
echo '' | base64 -d > icon48.png

# Create 128x128 icon
echo '' | base64 -d > icon128.png
